class ShedRow(object):
    def __init__(self, dayTime, dayDate, disciplName, disciplType, audNum, buildNum, prepodName):
        self.dayTime = dayTime
        self.dayDate = dayDate
        self.disciplName = disciplName
        self.disciplType = disciplType
        self.audNum = audNum
        self.buildNum = buildNum
        self.prepodName = prepodName





def TimetableWrite(groupId):
    isNormal, response = getResponse(groupId)
    if not isNormal:
        return response

    rows = 0
    lis = []

    elem = response[str(1)]
    lis.append("Понедельник")
    for day in elem:
        lis.append(ShedRow(day["dayTime"], day["dayDate"], day["disciplName"], day["disciplType"], day["audNum"],
                           day["buildNum"], day["prepodName"]))
    elem = response[str(2)]
    lis.append("Вторник")
    for day in elem:
        lis.append(ShedRow(day["dayTime"], day["dayDate"], day["disciplName"], day["disciplType"], day["audNum"],
                           day["buildNum"], day["prepodName"]))
    rows = len(lis)
    elem = response[str(3)]
    lis.append("Среда")
    for day in elem:
        lis.append(ShedRow(day["dayTime"], day["dayDate"], day["disciplName"], day["disciplType"], day["audNum"],
                           day["buildNum"], day["prepodName"]))
    rows = len(lis)
    elem = response[str(4)]
    lis.append("Четверг")
    for day in elem:
        lis.append(ShedRow(day["dayTime"], day["dayDate"], day["disciplName"], day["disciplType"], day["audNum"],
                           day["buildNum"], day["prepodName"]))
    rows = len(lis)
    elem = response[str(5)]
    lis.append("Пятница")
    for day in elem:
        lis.append(ShedRow(day["dayTime"], day["dayDate"], day["disciplName"], day["disciplType"], day["audNum"],
                           day["buildNum"], day["prepodName"]))
    rows = len(lis)
    elem = response[str(6)]
    lis.append("Суббота")
    for day in elem:
        lis.append(ShedRow(day["dayTime"], day["dayDate"], day["disciplName"], day["disciplType"], day["audNum"],
                           day["buildNum"], day["prepodName"]))

    return lis


def createDocShedule(group):
    # wordDocument= docx.Document()
    wordDocument = docx.Document("blank.docx")
    lis = TimetableWrite(group)
    for day in lis:
        if day in ["Понедельник", "Вторник", "Среда", "Четверг", "Пятница", "Суббота"]:
            par = wordDocument.add_heading(day, 3)
            # par = wordDocument.add_paragraph(day)
            par.bold = True



        else:
            par = wordDocument.add_paragraph(
                (str(day.dayTime)).rstrip() + " " + ((str(day.dayDate)).rstrip()).ljust(8) + " " + str(
                    day.disciplName) + " " + (str(day.disciplType)).upper() + " " + (
                    str(day.audNum)).rstrip() + " ауд  " + (str(day.buildNum)).rstrip() + "зд.  " + (
                    str(day.prepodName)).rstrip())
            # par.style = "No Spacing"
    wordDocument.save(str(group) + ".docx")





createDocShedule(123)